DROP TABLE IF EXISTS `tabelnegara`;

CREATE TABLE `tabelnegara` (
	`id` int(11) unsigned NOT NULL AUTO_INCREMENT,
	`negara` varchar(100) NOT NULL DEFAULT '',
	`alpha2` varchar(3) NOT NULL DEFAULT '',
	`alpha3` varchar(4) NOT NULL DEFAULT '',
	`numerik` varchar(5) NOT NULL DEFAULT '',
	PRIMARY KEY (`id`),
	KEY `kodenegara` (`alpha2`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


